import * as React from "react"
import { useState } from "react"
import { Progress } from "../Progress"

function SmartComponentMock(props) {
    const [prog, setProg] = useState(0.1)
    return (
        <div style={{ display: "contents" }} onClick={() => setProg(Math.random() * 100)}>
            <Progress shouldAnimate={false} progress={prog} type="line" />
        </div>
    )
}
